INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (1,1,'AminoLIFT Amino acids','AminoLIFT amino acid supllement with caffeine from USPLabs. Aminos. Energy. Focus. With Essential Amino Acids. 1000% RDI of Key B Vitamins. Clean Energy & Focus. Non GMO, Gluten Free. Fruit Punch flavor.',10.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (2,2,'C4 Extreme','C4 Extreme dietary supplement from Celluloz Labs. Mystery flavor. 2 Servings.',10.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (3,3,'Celsius Berry','Celsius calory burning drink mix. Berry blast flavor. Burn more calories, healthy lasting energy. Clinically proven, reduces body fat, tastes great. Just add water',4.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (4,4,'Celsius Orange','Celsius orange drink mix. Orange flavor. Burn more calories, healthy lasting energy. Clinically proven, reduces body fat, tastes great. Just add water',6.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (5,5,'Stimul8 Ripping','Finaflex Stimul8 high performance dietary supplement. Tropical storm flavor. Endurance + ripping matrix. Unparalleled energy. Extreme potency. One serving.',6.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (6,6,'Forza Pro','Forza Pro rapid and extended release protein hybrid. Increase lean muscle, 30g of protein per serving. Gluten free.',6.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (7,7,'Kinetic BCAA SAA','Kinetic BCAA - SAA form Neon SPort. Lemon berry flavor dietary supplement. 1 serving. 10g.',11.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (8,8,'Quest Bar','Quest Bar protein bar. Coookies and cream flavor. Gluten free. Only 1g sugar, high fiber. Only 3g net carbs. No sugar added! 21 g protein.',4.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (9,9,'Gainer powder','Professional Strength gainer. 54g of sustained-release protein per serving! Perfect 2:1 carbohydrate to protein ratio! Enhanced with Aminogen. Increases muscle mass. Contains whole grain carbohydrates. Mixes instantly and tastes great. Sustained-release formula',12.99,'Y','Y',CURRENT_TIMESTAMP);
INSERT INTO BLC_SKU (SKU_ID,DEFAULT_PRODUCT_ID,NAME,LONG_DESCRIPTION,RETAIL_PRICE,TAXABLE_FLAG,DISCOUNTABLE_FLAG,ACTIVE_START_DATE) VALUES (10,10,'XP2 fat burner','XP2 FEM FIRE. Intensify your energy levels, set your metabolism on fire and experience the best workout of your life. Designed to enhance fat loss, training intensity, strength, focus, energy levels, performance and endurance. Not intended for beginners. Use under strict dosing protocol!',12.99,'Y','Y',CURRENT_TIMESTAMP);
